  def self.scrape_state_names(url)
    
    arr = []
   
   doc = Nokogiri::HTML(open(url))
  
 # binding.pry 
  doc.css('select#form-park option').each do |state_name|
    
    next if state_name.text == "State"
    hash = {}
    hash[:name] = state_name.text
    hash[:value] = state_name.css('/@value').text
    arr << hash
  end
  
  return arr
  
  
